import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{e as a}from"./app.5281c868.js";const i={},t=a('<h2 id="_1-\u4E3A\u4EC0\u4E48\u9700\u8981https" tabindex="-1"><a class="header-anchor" href="#_1-\u4E3A\u4EC0\u4E48\u9700\u8981https" aria-hidden="true">#</a> 1. \u4E3A\u4EC0\u4E48\u9700\u8981HTTPS\uFF1F</h2><p>HTTPS \u662F\u5B89\u5168\u7248\u7684 HTTP\u3002</p><p>HTTP \u534F\u8BAE\u5728\u4F20\u8F93\u6570\u636E\u65F6\u91C7\u7528\u7684\u662F\u660E\u2F42\u65B9\u5F0F\u4F20\u9012\uFF0C\u56E0\u6B64\uFF0C\u2F00\u4E9B\u654F\u611F\u4FE1\u606F\u7684\u4F20\u8F93\u5C31\u53D8\u5F97\u5F88\u4E0D\u5B89\u5168\u3002</p><p>\u800C HTTPS \u5C31\u662F\u4E3A\u4E86\u89E3\u51B3 HTTP \u7684\u4E0D\u5B89\u5168\u2F7D\u4EA7\u2F63\u7684\u3002</p><h2 id="_2-https\u662F\u5982\u4F55\u4FDD\u8BC1\u5B89\u5168\u7684" tabindex="-1"><a class="header-anchor" href="#_2-https\u662F\u5982\u4F55\u4FDD\u8BC1\u5B89\u5168\u7684" aria-hidden="true">#</a> 2. HTTPS\u662F\u5982\u4F55\u4FDD\u8BC1\u5B89\u5168\u7684\uFF1F</h2><p>HTTPS \u5728\u4F20\u8F93\u6570\u636E\u7684\u8FC7\u7A0B\u4E2D\u4F1A\u5BF9\u6570\u636E\u8FDB\u884C\u52A0\u5BC6\u5904\u7406\uFF0C\u4FDD\u8BC1\u5B89\u5168\u6027\u3002</p><p>\u90A3HTTPS\u91C7\u7528\u7684\u4EC0\u4E48\u6837\u7684\u52A0\u5BC6\u65B9\u5F0F\u5462\uFF1F\u6211\u4EEC\u6765\u4E86\u89E3\u4E0B\u4E00\u4E9B\u52A0\u5BC6\u7684\u57FA\u672C\u6982\u5FF5\u3002</p><p>\u76EE\u524D\u5E38\u89C1\u7684\u52A0\u5BC6\u7B97\u6CD5\u53EF\u4EE5\u5206\u6210\u4E09\u7C7B\uFF0C\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\uFF0C\u975E\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u548CHash\u7B97\u6CD5\u3002</p><h3 id="_2-1-\u4EC0\u4E48\u662F\u5BF9\u79F0\u52A0\u5BC6" tabindex="-1"><a class="header-anchor" href="#_2-1-\u4EC0\u4E48\u662F\u5BF9\u79F0\u52A0\u5BC6" aria-hidden="true">#</a> 2.1 <strong>\u4EC0\u4E48\u662F\u5BF9\u79F0\u52A0\u5BC6\uFF1F</strong></h3><p>\u5BF9\u79F0\u52A0\u5BC6\u7684\u7279\u70B9\u662F\u6587\u4EF6\u52A0\u5BC6\u548C\u89E3\u5BC6\u4F7F\u7528\u76F8\u540C\u7684\u5BC6\u94A5\uFF0C\u5373\u52A0\u5BC6\u5BC6\u94A5\u4E5F\u53EF\u4EE5\u7528\u4F5C\u89E3\u5BC6\u5BC6\u94A5\uFF0C</p><p>\u8FD9\u79CD\u65B9\u6CD5\u5728\u5BC6\u7801\u5B66\u4E2D\u53EB\u505A\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\uFF0C\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u4F7F\u7528\u8D77\u6765\u7B80\u5355\u5FEB\u6377\uFF0C\u5BC6\u94A5\u8F83\u77ED\uFF0C\u4E14\u7834\u8BD1\u56F0\u96BE</p><p>\u901A\u4FE1\u7684\u53CC\u2F45\u90FD\u4F7F\u2F64\u540C\u2F00\u4E2A\u79D8\u94A5\u8FDB\u2F8F\u52A0\u89E3\u5BC6\u3002\u2F50\u5982\uFF0C\u4E24\u4E2A\u4EBA\u4E8B\u5148\u7EA6\u5B9A\u7684\u6697\u53F7\uFF0C\u5C31\u5C5E\u4E8E\u5BF9\u79F0\u52A0\u5BC6\u3002</p><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527233628.png" alt="img" loading="lazy"></p><p>\u5BF9\u79F0\u52A0\u5BC6\u7684\u7279\u70B9\u662F\uFF1A</p><ul><li><p>\u4F18\u70B9: \u8BA1\u7B97\u91CF\u5C0F\u3001\u52A0\u5BC6\u901F\u5EA6\u5FEB\u3001\u52A0\u5BC6\u6548\u7387\u9AD8\u3002</p></li><li><p>\u7F3A\u70B9: \u5728\u6570\u636E\u4F20\u9001\u524D\uFF0C\u53D1\u9001\u65B9\u548C\u63A5\u6536\u65B9\u5FC5\u987B\u5546\u5B9A\u597D\u79D8\u94A5\uFF0C\u7136\u540E\u53CC\u65B9\u4FDD\u5B58\u597D\u79D8\u94A5\u3002 \u5982\u679C\u4E00\u65B9\u7684\u79D8\u94A5\u88AB\u6CC4\u9732\uFF0C\u90A3\u4E48\u52A0\u5BC6\u4FE1\u606F\u4E5F\u5C31\u4E0D\u5B89\u5168\u4E86</p></li></ul><p>\u4F7F\u7528\u573A\u666F\uFF1A\u672C\u5730\u6570\u636E\u52A0\u5BC6\u3001https\u901A\u4FE1\u3001\u7F51\u7EDC\u4F20\u8F93\u7B49</p><p>\u5E38\u89C1\u7B97\u6CD5\uFF1AAES\u3001DES\u30013DES\u3001DESX\u3001Blowfish\u3001IDEA\u3001RC4\u3001RC5\u3001RC6</p><h3 id="_2-2-\u4EC0\u4E48\u662F\u975E\u5BF9\u79F0\u52A0\u5BC6" tabindex="-1"><a class="header-anchor" href="#_2-2-\u4EC0\u4E48\u662F\u975E\u5BF9\u79F0\u52A0\u5BC6" aria-hidden="true">#</a> 2.2 <strong>\u4EC0\u4E48\u662F\u2FAE\u5BF9\u79F0\u52A0\u5BC6\uFF1F</strong></h3><p>\u800C\u52A0\u5BC6\u548C\u89E3\u5BC6\u5176\u5B9E\u53EF\u4EE5\u4F7F\u7528\u4E0D\u540C\u7684\u89C4\u5219\uFF0C\u53EA\u8981\u8FD9\u4E24\u79CD\u89C4\u5219\u4E4B\u95F4\u5B58\u5728\u67D0\u79CD\u5BF9\u5E94\u5173\u7CFB\u5373\u53EF\uFF0C</p><p>\u8FD9\u6837\u5C31\u907F\u514D\u4E86\u76F4\u63A5\u4F20\u9012\u5BC6\u94A5\u3002\u8FD9\u79CD\u65B0\u7684\u52A0\u5BC6\u6A21\u5F0F\u88AB\u79F0\u4E3A&quot;\u975E\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5&quot;\u3002</p><p>\u901A\u4FE1\u7684\u53CC\u65B9\u4F7F\u7528\u4E0D\u540C\u7684\u79D8\u94A5\u8FDB\u884C\u52A0\u5BC6\u89E3\u5BC6\uFF0C\u5373\u79D8\u94A5\u5BF9\uFF08\u79C1\u94A5 + \u516C\u94A5\uFF09\u3002</p><p>\u7279\u5F81: \u79C1\u94A5\u53EF\u4EE5\u89E3\u5BC6\u516C\u94A5\u52A0\u5BC6\u7684\u5185\u5BB9, \u516C\u94A5\u53EF\u4EE5\u89E3\u5BC6\u79C1\u94A5\u52A0\u5BC6\u7684\u5185\u5BB9</p><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527234705.png" alt="img" loading="lazy"></p><p>\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u7279\u70B9\u662F\uFF1A</p><ul><li><p>\u4F18\u70B9\uFF1A\u975E\u5BF9\u79F0\u52A0\u5BC6\u4E0E\u5BF9\u79F0\u52A0\u5BC6\u76F8\u6BD4\u5176\u5B89\u5168\u6027\u66F4\u597D</p></li><li><p>\u7F3A\u70B9\uFF1A\u52A0\u5BC6\u548C\u89E3\u5BC6\u82B1\u8D39\u65F6\u95F4\u957F\u3001\u901F\u5EA6\u6162\uFF0C\u53EA\u9002\u5408\u5BF9\u5C11\u91CF\u6570\u636E\u8FDB\u884C\u52A0\u5BC6\u3002</p></li></ul><p>\u4F7F\u7528\u573A\u666F\uFF1Ahttps\u4F1A\u8BDD\u524D\u671F\u3001CA\u6570\u5B57\u8BC1\u4E66\u3001\u4FE1\u606F\u52A0\u5BC6\u3001\u767B\u5F55\u8BA4\u8BC1\u7B49</p><p>\u5E38\u89C1\u7B97\u6CD5\uFF1ARSA\u3001ECC\uFF08\u79FB\u52A8\u8BBE\u5907\u7528\uFF09\u3001Diffie-Hellman\u3001El Gamal\u3001DSA\uFF08\u6570\u5B57\u7B7E\u540D\u7528\uFF09</p><blockquote><p>https\u52A0\u5BC6\u8FC7\u7A0B\u4E2D\uFF0C\u79D8\u94A5\u548C\u79C1\u94A5\u7684\u4F20\u9012</p></blockquote><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/image-20220220093912230.png" alt="image-20220220093912230" style="zoom:50%;"><h3 id="_2-3-https-\u52A0\u5BC6\u89E3\u51B3\u65B9\u6848" tabindex="-1"><a class="header-anchor" href="#_2-3-https-\u52A0\u5BC6\u89E3\u51B3\u65B9\u6848" aria-hidden="true">#</a> 2.3 <strong>HTTPS \u52A0\u5BC6\u89E3\u51B3\u2F45\u6848</strong></h3><p>\u7ED3\u5408\u4E86\u4E24\u79CD\u52A0\u5BC6\u2F45\u5F0F\uFF1A</p><ul><li><p>\u5C06 <code>\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5</code> \u2F64\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u516C\u94A5, \u8FDB\u2F8F\u52A0\u5BC6\u5E76\u53D1\u9001\u51FA\u53BB\uFF0C\u63A5\u6536\u2F45\u4F7F\u2F64\u79C1\u94A5\u89E3\u5BC6\u5F97\u5230 <code>\u5BF9\u79F0\u52A0\u5BC6\u5BC6\u94A5</code></p></li><li><p>\u53CC\u2F45\u6C9F\u901A\u65F6\u4F7F\u2F64 <code>\u5BF9\u79F0\u52A0\u5BC6\u5BC6\u94A5</code> \u8FDB\u2F8F</p></li></ul><p><strong>\u53EF\u4EE5\u770B\u5230\uFF0C\u53EA\u6709\u5728\u53D1\u9001\u79D8\u94A5\u9636\u6BB5\u624D\u4F7F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u800C\u540E\u7EED\u7684\u901A\u4FE1\u90FD\u4F7F\u7528\u5BF9\u79F0\u52A0\u5BC6\uFF0C\u8FD9\u6837\u89E3\u51B3\u4E86\u6027\u80FD\u95EE\u9898\u3002</strong></p><p>HTTPS \u76EE\u524D\u6240\u4F7F\u7528\u7684 TLS\u6216SSL\u534F\u8BAE, \u5C31\u662F\u76EE\u524D\u91C7\u7528\u7684\u52A0\u5BC6\u901A\u9053\u7684\u89C4\u8303\u534F\u8BAE</p><p>\u5B83\u5229\u7528\u5BF9\u79F0\u52A0\u5BC6\u3001(\u516C\u79C1\u94A5)\u975E\u5BF9\u79F0\u52A0\u5BC6, \u4EE5\u53CA\u5176\u5BC6\u94A5\u4EA4\u6362\u7B97\u6CD5\uFF0C\u53EF\u5B8C\u6210\u53EF\u4FE1\u4EFB\u7684\u4FE1\u606F\u4F20\u8F93</p><h3 id="_2-4-\u6570\u5B57\u8BC1\u4E66" tabindex="-1"><a class="header-anchor" href="#_2-4-\u6570\u5B57\u8BC1\u4E66" aria-hidden="true">#</a> 2.4 \u6570\u5B57\u8BC1\u4E66</h3><p>\u4E3A\u4E86\u5B89\u5168\u6027, \u4E00\u822C\u8FD8\u9700\u8981\u7B7E\u53D1\u6570\u5B57\u8BC1\u4E66!</p><p>\u5BA2\u6237\u7AEF \u548C \u670D\u52A1\u5668\u7AEF\u8981\u521D\u6B65\u4E92\u901A\u6D88\u606F\u65F6, \u5BA2\u6237\u7AEF\u53D1\u9001\u8BF7\u6C42\u53EF\u4EE5\u62FF\u5230\u516C\u5F00\u7684\u516C\u94A5\u4FE1\u606F</p><p>\u8FDB\u800C\u8FDB\u884C\u975E\u5BF9\u79F0\u52A0\u5BC6, \u4F7F\u7528\u516C\u94A5, \u52A0\u5BC6<code>\u5BF9\u79F0\u52A0\u5BC6\u5BC6\u94A5</code>, \u4F20\u9012\u7ED9\u670D\u52A1\u5668, \u540E\u7EED\u901A\u4FE1\u90FD\u4F7F\u7528\u5BF9\u79F0\u52A0\u5BC6!</p><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527235153.png" alt="img" loading="lazy"></p><p><strong>\u95EE\u9898\u662F: \u521D\u6B65\u4E92\u901A\u6D88\u606F\u65F6, \u5982\u679C\u8BF7\u6C42\u62FF\u5230\u7684\u516C\u94A5\u4FE1\u606F, \u5C31\u662F\u5047\u7684, \u6216\u8005\u4E0D\u5B89\u5168\u7684! \u90A3\u4E48\u540E\u7EED\u7684\u6240\u6709\u64CD\u4F5C, \u90FD\u5C06\u662F\u4E0D\u5B89\u5168\u7684!</strong></p><p>\u6240\u4EE5, \u5C31\u9700\u8981\u6709\u6570\u5B57\u8BC1\u4E66(CA\u8BC1\u4E66), \u4E00\u822C\u662FCA\u673A\u6784\u9881\u53D1\u7684, \u8BC1\u660E\u8FD9\u4E2A\u516C\u94A5\u662F\u5B89\u5168\u53EF\u9760\u7684!</p><p><strong>CA\u8BC1\u4E66\u4E2D\u5FC3\u4F1A\u5BF9\u4F60\u7F51\u7AD9\u7684\u516C\u94A5, \u7F51\u7AD9\u7684\u57DF\u540D\u5730\u5740, \u8BC1\u4E66\u5230\u671F\u65F6\u95F4, \u7B49\u4E00\u4E9B\u76F8\u5173\u4FE1\u606F\u4E00\u8D77\u52A0\u5BC6\u7B7E\u53D1\u6570\u5B57\u8BC1\u4E66, \u4FDD\u8BC1\u4F60\u7F51\u7AD9\u7684\u5B89\u5168\u6027</strong></p><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527233637.png" alt="img" loading="lazy"></p><p>\u5F53\u516C\u53F8\u7533\u8BF7\u4E86 CA \u8BC1\u4E66\u540E, \u5C31\u5E94\u8BE5\u5728\u54CD\u5E94\u65F6, \u5C06\u6570\u5B57\u8BC1\u4E66\u4E00\u8D77\u53D1\u9001\u7ED9\u5BA2\u6237\u7AEF</p><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527233641.png" alt="img" loading="lazy"></p><p>\u800C\u5BA2\u6237\u7AEF, \u63A5\u6536\u5230\u6D88\u606F\u540E, \u5C31\u53EF\u4EE5\u67E5\u770B\u8BC1\u4E66</p><ol><li><p>\u5982\u679C\u6B63\u5728\u8BBF\u95EE\u7684\u7F51\u7AD9 \u548C \u8BC1\u4E66\u8BB0\u8F7D\u7684\u7F51\u5740 \u4E0D\u4E00\u81F4, \u8BF4\u660E\u4E0D\u5B89\u5168, \u53EF\u80FD\u88AB\u5192\u7528, \u6D4F\u89C8\u5668\u5C31\u4F1A\u53D1\u51FA\u8B66\u544A!!!</p></li><li><p>\u5982\u679C\u7B7E\u53D1\u8BC1\u4E66\u7684\u673A\u6784, \u4E0D\u6743\u5A01, \u53D1\u51FA\u8B66\u544A <img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/20210527233644.png" alt="img" loading="lazy"></p></li><li><p>\u5982\u679C\u8BC1\u4E66\u8FC7\u671F\u4E86, \u6D4F\u89C8\u5668\u4E5F\u4F1A\u53D1\u51FA\u8B66\u544A</p></li></ol><blockquote><p>https\u4E3A\u4EC0\u4E48\u8FDB\u884C\u8BC1\u4E66\u6821\u9A8C</p></blockquote><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/image-20220220093946466.png" alt="image-20220220093946466" style="zoom:50%;"><h3 id="_2-5-\u6570\u5B57\u7B7E\u540D" tabindex="-1"><a class="header-anchor" href="#_2-5-\u6570\u5B57\u7B7E\u540D" aria-hidden="true">#</a> 2.5 \u6570\u5B57\u7B7E\u540D</h3><p>\u4F46\u8FD9\u8FD8\u662F\u6709\u95EE\u9898\uFF1A\u5982\u679C\u8BC1\u4E66\u88AB\u7BE1\u6539\u4E86\u600E\u4E48\u529E?</p><p>\u8FD9\u65F6\u5C31\u9700\u8981\u7528\u2F00\u4E2A\u6280\u672F\uFF1A<strong>\u6570\u5B57\u7B7E\u540D</strong>\u3002 (\u6839\u636E\u8BC1\u4E66\u5185\u5BB9, \u751F\u6210\u7684\u4E00\u4E2A\u552F\u4E00\u6807\u8BC6)</p><p>\u6570\u5B57\u7B7E\u540D\u5C31\u662F\u5148\u2F64 <strong>CA \u2F83\u5E26\u7684 Hash \u7B97\u6CD5\u6765\u8BA1\u7B97\u51FA\u8BC1\u4E66\u5185\u5BB9\u7684\u2F00\u4E2A\u6458\u8981</strong>\uFF0C\u7136\u540E\u4F7F\u2F64 CA \u79C1\u94A5\u8FDB\u884C\u52A0\u5BC6\uFF0C\u7EC4\u6210\u6570\u5B57\u7B7E\u540D\u3002</p><p>\u5F53\u522B\u2F08\u628A\u4ED6\u7684\u8BC1\u4E66\u53D1\u8FC7\u6765\u65F6\uFF0C<strong>\u63A5\u6536\u65B9\u2F64\u540C\u6837\u7684\u7B97\u6CD5\u518D\u6B21\u2F63\u6210\u6458\u8981</strong>\uFF0C\u2F64 CA \u516C\u94A5\u89E3\u5BC6\u540E\u5F97\u5230CA\u751F\u6210\u7684\u6458\u8981\uFF0C\u4E24\u8005\u8FDB\u884C\u5BF9\u2F50\u540E,</p><p>\u5C31\u80FD\u786E\u5B9A\u4E2D\u95F4\u662F\u5426\u88AB\u2F08\u7BE1\u6539\u3002\u8FD9\u6837\u5C31\u80FD\u6700\u2F24\u7A0B\u5EA6\u7684\u4FDD\u8BC1\u901A\u4FE1\u7684\u5B89\u5168\u4E86\u3002</p><blockquote><p>\u6570\u5B57\u7B7E\u540D\u7684\u4F5C\u7528</p></blockquote><p><img src="https://wuxiaohui-1254415986.cos.ap-nanjing.myqcloud.com/uPic/image-20220220094005031.png" alt="image-20220220094005031" loading="lazy"></p>',58);function o(n,s){return t}var l=p(i,[["render",o],["__file","\u62D3\u5C55-https.html.vue"]]);export{l as default};
